mapper.select:
SELECT emp.DepName AS orgName, SUM(p.P_Count) AS targetNum, COUNT(o.YJOrg) AS realNum, (SUM(p.P_Count) * 3.33) AS targetPaperNum, COUNT(os.TradeItem_Auto) AS proPaperNum, (SUM(p.P_Count) * (SELECT Num FROM [ItemCode] WHERE ItemType = 10000 + #{year})) AS targetVolume, SUM(o.MAmt * o.MM) AS realVolume FROM [v_Emp] AS emp LEFT JOIN [Performance] AS p ON p.Sales_Auto = emp.User_Auto LEFT JOIN [Order] AS o ON p.Sales_Auto = o.Sales_Auto LEFT JOIN [Orders] AS os ON p.Sales_Auto = os.Sales_Auto WHERE 1 = 1
AND p.P_Year = #{year}
AND p.P_Month = #{month}
AND emp.UpUnit = #{orgAuto}
AND os.AppDate BETWEEN #{startDate} AND #{endDate} GROUP BY emp.DepName

SELECT emp.User_Auto AS salesAuto,emp.FName AS fName, SUM(p.P_Count) AS targetNum, COUNT(o.YJOrg) AS realNum, (SUM(p.P_Count) * 3.33) AS targetPaperNum, COUNT(os.TradeItem_Auto) AS proPaperNum, (SUM(p.P_Count) * (SELECT Num FROM [ItemCode] WHERE ItemType = 10000 + #{year})) AS targetVolume, SUM(o.MAmt * o.MM) AS realVolume FROM [v_Emp] AS emp LEFT JOIN [Performance] AS p ON p.Sales_Auto = emp.User_Auto LEFT JOIN [Order] AS o ON p.Sales_Auto = o.Sales_Auto LEFT JOIN [Orders] AS os ON p.Sales_Auto = os.Sales_Auto WHERE 1 = 1
AND p.P_Year = #{year}
AND p.P_Month = #{month}
AND os.AppDate BETWEEN #{startDate} AND #{endDate} GROUP BY emp.User_Auto,emp.FName

SELECT emp.DepName AS orgName, SUM(p.P_Count) AS targetNum, COUNT(o.YJOrg) AS realNum, (SUM(p.P_Count) * 3.33) AS targetPaperNum, COUNT(os.TradeItem_Auto) AS proPaperNum, (SUM(p.P_Count) * (SELECT Num FROM [ItemCode] WHERE ItemType = 10000 + #{year})) AS targetVolume, SUM(o.MAmt * o.MM) AS realVolume FROM [v_Emp] AS emp LEFT JOIN [Performance] AS p ON p.Sales_Auto = emp.User_Auto LEFT JOIN [Order] AS o ON p.Sales_Auto = o.Sales_Auto LEFT JOIN [Orders] AS os ON p.Sales_Auto = os.Sales_Auto WHERE 1 = 1
AND p.P_Year = #{year}
AND p.P_Month = #{month}
AND p.Sales_Auto = #{userAuto}
AND os.AppDate BETWEEN #{startDate} AND #{endDate} GROUP BY emp.DepName
